    public void afficheMap(TilePane tilepane) {
        for (int i :world
             ) {
            System.out.println(i);

        }
        Image tileset = new Image(String.valueOf(getClass().getResource("/Tileset.png")));
        Image tileset2 = new Image(String.valueOf(getClass().getResource("/air.jpg")));



        for (int j=0; j < this.world.length; j++) {
            ImageView terre = new ImageView(tileset);
            ImageView ciel = new ImageView(tileset2);

            int ligne = j/16;
            int colonne = (j%16)-1;

            int y = ligne * 32;
            int x = colonne * 32;

            if ((int)this.world[j] != (int)0) {
                terre.setViewport(new Rectangle2D(x, y, 64, 64));
                tilepane.getChildren().add(terre);
            } else {
                ciel.setViewport(new Rectangle2D(x, y, 64, 64));
                tilepane.getChildren().add(ciel);
            }

        }
    }
